#!/usr/bin/env python3""" Main file """import loggingimport osimport sysfrom app.startup import Startupif hasattr(sys, 'frozen'):    root_dir = os.path.dirname(os.path.abspath(sys.executable))else:    root_dir = os.path.dirname(os.path.abspath(__file__))try:    Startup().setup(root_dir).start()except KeyboardInterrupt as e:    logging.debug(e)